mapper.update:
UPDATE meta_data_product SET name = #{name}, category = #{category}, unit = #{unit}, cost = #{cost}, price = #{price}, production_status = #{production_status}
, ofileName = #{ofileName}
WHERE product_code = #{product_code}

mapper.select:
select count(*) from meta_data_product



and regdate between #{startDate} and #{endDate}



and name like '%${search }%'

select * from meta_data_product



and regdate between #{startDate} and #{endDate}



and name like '%${search }%'



limit #{cri.startPage},#{cri.pageSize}

select code_code
from common_code
where code_name = #{category}
